def caps_number(journalists)
    count = 0
    journalists.each do |i|
    
    
       
        if i[1] != i[1].downcase
            count +=1
        end

    end
    puts "Il y à #{count} emails qui commencent par une majuscule !"
end
caps_number(journalists)



def find_handle(journalists)
    puts "Quel est la position dans l'array de : @epenser"
    count = 0
    journalists.each do |i|
        if i != "@epenser"
            count += 1
        end
        if i == "@epenser"
            puts "@epenser se trouve à la place n#{count}" 
            return count
        end
    end
end
find_handle(journalists)

def classing_handle(journalists)
    puts "Repartition des handle par taille : "
    nouvelle=Array.new
    maximum = journalists.max_by(&:length)
    n = maximum.length
    for j in (0..n)
        count = 0
        journalists.each do |i|
            if i[1..-1].length == j
                count += 1
            end
        
        end
        puts "Il existe #{count} emails de  #{j} caractères"
    end
end

classing_handle(journalists)